manifest.init:
function ($o, form){
		// editor component container
		$o.html('<div id="meditor" class="dib vat"></div>'
			+'<div id="control" class="w200 dib vat ml20"></div>'
		);

		//right pane
		$o.find("#control")
		.html($.my.formgen([
		  {row:"220px",label:"50px",rowCss:"my-row pb5"},
			['', 
			 'btn#btn-save.tac.green.fs110.br100.mr5.ml-5.w100',{val:"Save"},
			 'btn#btn-close.tac.fs110.br100.w100',{val:"Close"}
			],
			//encrypt
			'<div class="mt15"><select id="encrypt" class="fs90 w190"></select></div>',
			//tags
			'<div class="mt10"><input type="text" id="doctags" class="fs80 w190"></div>',
			//attachments
			'<div id="atts" class="fs80 gray mt15"></div>',
			// html generator
			this.Export?'<div class="fs80 mt5">'+
			'<span class="pseudolink" id="btn-export">Generate HTML</span>'+
			'</div>':''
		]));

		//enc key
		form.data.key= form.data.editor.crypto||"_";
	}

