#atts.init:
function ($o, form) {
				$o.my("cw.Sys.Attachments", {
					doc:form.data.editor,
					// truncate file names, optional
					truncate:24,
					// sort and filter function, optional
					sort: function (attObj, fname){
						//sort by type, than by name
						var ext = fname;
						// filter hidden
						return /^\./.test(fname)?null:ext;
					}
				});
			}

#btn-save.bind:
function (d,v,$o){
				var that=this;
				if (v!=null) {

					// Here we can call 
					// 		$o.trigger("commit");
					// But its reasonable to save manually to
					// avoid full redraw, which is costly
					// dut to plenty of ‘heavy’ controls.

					that.db.save(/*d.editor*/)
					.then(function (res){
							Object.merge(
								d.editor,
								Object.select(res,[/^_/, "stamp", "log"])
							);
							$o.my("find","#atts").trigger("recalc");
					})
					.fail(function (e,msg) {
						console.log(msg, e);

					});
				}
			}

Editor.Hoverpane:
function (type, del){
		// Hoverpane generator, enumerates all plugins
		var i, v,
				html= '<div class="cw-hoverpane cw-hoverpane-insert">'
					+'<span class="orange pr6 pl2 fs105 dib mt2 lh90">+</span>', 
				that = this, 
				mans = that.Items;
		for (i in mans) {
			v = mans[i];
			if (!v.NoSelfInline || type!=v.data.type) {
				html+='<span data-type="'+v.data.type+'" class="pseudolink cw-btn-small">'+v.Name+'</span>';
			}
		}
		if (del) html += '<span class="pseudolink cw-btn-small red" data-type="-" id="btn-del">Del</span>';
		html+='</div>';
		return html;
	}

Editor.init:
function ($o, form) {
	$o.html($.my.formgen([
		{row:"750px",label:"100px"},
		'<div class="cb bbs pb10 mb20"><textarea id="title" '
		+'rows=1 class="gray fs190  w750" '
		+'style="border:none;padding:0;margin:0;box-shadow:none;'
		+'color:#333;min-height:0;resize:none;" '
		+'placeholder="Title"></textarea>'
		+'</div>',
		'<div id="medium" class="cb mt10 w750"></div>'
	])
				 );

	if (!$o.find("#hoverpane:eq(0)").size()) {
		$($(this.Hoverpane()).attr("id","hoverpane")[0].outerHTML)
		.prependTo($o);
	}

	var that = this, i,
			d = form.data,
			now = Date.now(),
			$ho=$o.find("#hoverpane:eq(0)"),
			hh = $ho.height(),
			$me = $o.find("#medium"),
			dx = $o.offset().left,
			dy = $o.offset().top;

	for (i=0;i<d.content.length;i++) if (!d.content[i]._stamp) d.content[i]._stamp = (++now)+"";

	this.Insert = function  (type, idx, p, after) {
		var ins,txt, a=[], od = $o.my("data"), onew={};

		if (p) {
			txt = od.content[idx].data;
			a=txt.split(p);
		}

		if (type) {
			ins = $.extend(true, {_new:true, _stamp:Date.now()+""}, that.Items[type].data);
		}

		if (!p || a.length<2 || (a[0]=="" && !after) || (a[1]=="" && after)) {
			if (type) od.content.add(ins, idx+(after?1:0));
			else od.content.removeAt(idx);
		} else if (p) {
			if (type) {
				od.content[idx].data=a[0]+(after?p:"");
				onew=Object.clone(od.content[idx],true);
				onew.data=(!after?p:"")+a[1];
				onew._stamp = (Date.now()+12)+"";
				od.content.add(onew, idx+1);
				od.content.add(ins, idx+1);
			} else {
				//delete
				if (!a[0].length && !a[1].length) {
					od.content.removeAt(idx+(after?1:0));
				} else {
					od.content[idx].data=a[0]+a[1];
				}
			}
		}
		//$o.my("redraw")
		$o.find("#medium").trigger("recalc");
	}

	//hoverpane actions

	$o.on("click", ".cw-hoverpane-insert .pseudolink", function (){
		var xd = $ho.data("hoverpane"),
				$r = xd.form,
				idx = ($r.data("formlist")?$r.data("formlist").index:$o.my("data").content.length+1),
				type = $(this).data("type");

		if (type!="-") that.Insert(type, idx, xd.p, xd.after);
		else {
			$r.trigger("remove");
			//that.Insert(null, idx, xd.p, xd.after);
		}
		$ho.removeClass("cw-half").css({top:"-200px",left:0});

	});

	// hoverpane tracker

	$me.on(
		"mousemove.my", 
		Object.values(that.Items)
		.map(function(e){return e.Hoverpane})
		.compact()
		.join(", "), 
		function (evt) {
			var dx = $o.offset().left,
					dy = $o.offset().top,
					scy = $o.scrollTop(),
					$x = $(this), 
					ofs = $x.offset(), 
					h=$x.height(),
					ex = evt.clientX, 
					ey = evt.clientY,
					x = ofs.left,
					y = ofs.top, 
					after=false, 
					fulltop=0, 
					$form, type, ohtml="", fhtml="";
			//if (ey-y>h/2) y=y+(h+hh-2), after=true;
			hh = $ho.height();
			//correct pos if we inside Redactor, that has floating content
			if ($x.is(".redactor_box *")) {
				//insert and del small shim
				var $i=$('<b>\u200b</b>').prependTo($x);
				x=$i.offset().left;
				$i.remove();
			}

			fulltop = y-dy-hh+scy; 
			if ($ho.data("hoverpane") && $ho.data("hoverpane").top && Math.abs($ho.data("hoverpane").top-fulltop)<3) 
				fulltop = $ho.data("hoverpane").top;

			$ho.css({top:fulltop+"px", left:(x-dx-29)+"px"});

			$ho.toggleClass("cw-half", (ex-x<100 /*&& Math.abs(y+h/2-ey+scy)-h/2<40*/));

			$form = $x.parents(".my-form:eq(0)");
			type = $form.my("data").type;

			if (type=="html") {
				ohtml=$x[0].outerHTML;
				fhtml=$form.my("data").data;
			}

			$ho.html($(that.Hoverpane(type, fhtml===ohtml && ohtml.stripTags()==="")).html());

			if (type==="html") $ho.data("hoverpane",{form:$form, p:$x[0].outerHTML, after:after, top:fulltop});
			else $ho.data("hoverpane",{form:$form, after:after, top:fulltop});		

		}.debounce(50)
	);
			}

