img.init:
function ($o, form) {
		var d= form.data,
				data = d.data,
				fmt = d.format,
				style=this.Formats[fmt];
		$o.html(
			'<div class="cw-hoverpane-overlay cw-hoverpane">'
				+'<span id="format" class="fs80"></span>'
				+'<span class="fs80 pl5 pr5" id="showdesc">Desc</span>'
				//+'<span class="pseudolink pl5 pr5 red" id="btn-del">×</span>'
			+'</div>'
			+'<div id="imageframe" class="dib vat" style="min-height:30px;">'
				+(""===data?('<div class="p5 tac fs90 bg-yellow">'
					+'<span class="pseudolink">Image...</span>'
				 +'</div>'):"")
			+'</div>'
			+'<div id="descframe" class="dib vat"><textarea id="desc" placeholder:"Image text "></textarea></div>'
		);
		if (""!==data) $('<img class="cw-preview" src="" />')
