#imageframe.bind:
function (d,v,$o) {
				var that = this;
				if (v!=null || d._new) {
					$.my.modal({
						manifest:this.Cropper,
						data:{
							data:"",
							filename:"", 
							width:(d.format+"").replace(/\D/g,"")
						},
						width:820,
						esc:true
					}).then(function (crop) {
						if (Object.isObject(crop) && crop.data) {
							$o.html("");
							$('<img class="cw-preview" src=""/>')
							.attr("src","data:"+(crop.type||"image/jpeg")+";base64,"+crop.data)
							.appendTo($o);
							d.data=crop.data;
							d.format="left"+crop.width;
							crop.data="";
							$o.my().root.my("redraw");
						}
					}).fail(function(a,b){
						if (!$o.find("img").size()) {
							$o.my().root.my("remove",true);
						}
					});
					if (d._new) delete d._new;
				}
			}

#format.bind:
function (d,v,$o) {
				var $r, fm;
				if (v!=null) {
					$r = $o.my().root;
					fm = this.Formats[v];
					$r.removeClass("fl cl cr fr db dib di")
					.addClass(fm.css)
					.width(fm.frame);
					$r.find("#imageframe")
					.width(fm.image);
					$r.find("#descframe")
					.width(fm.desc)
					.toggleClass("ml20 mt-4", fm.frame!=fm.image);
					d.format=v;
					return v;
				}
				return d.format;
			}

#format.init:
function ($o) {
				var that=this, formats = that.Formats, empty={},
						e0=Object.keys(formats)[0];

				empty[formats[e0].text] = e0;
				$o.tags({tags:[Object.keys(formats).slice(1).map(function(e){
					var tag={};
					tag[formats[e].text]=e;
					return tag;
				})],
								 empty:empty});

			}

#file.bind:
function (d, v, $o){

	    		var f,
					$r = $o.my().root,
					$img = $r.find("#source"),
					img="";


				if (v!=null && v) {
					f = $o[0].files[0];
					if (f) (function(){
						d.filename = f.name;

						var fr = new FileReader(), ri, sb64 = [];
						fr.onload=function(e){
							ri = new Uint8Array(e.target.result);
							for (var i=0; i<ri.length; i++) sb64.push(String.fromCharCode(ri[i]));
							img = window.btoa(sb64.join(""));
						};

						fr.onloadend = function (){
							$img.load(function(){d.scale = $img[0].naturalWidth/$img.width();});
							$img.removeClass("hide")
								.attr("src","data:image/jpeg;base64,"+img);

							d.scale=$img[0].naturalWidth/$img.width();	
							$img.Jcrop({
								onChange: preview,
								onSelect: preview.debounce(20),
								allowMove:true
							}, function (){
