#btn-apply.bind:
function (d,v,$o) {
				var f,
					$r = $o.my().root,
					$c = $r.find("#x1"),
					$img = $r.find("#source"),
					$c2 = $r.find("#x2"),
					n={k:d.scale}, img="",
					width=d.width*1.5;

				if (v!=null && d.cropped) {
					preview(d.crop);

					d.data = $o.my("find","#x1")[0]
						.toDataURL('image/jpeg',0.95)
						.substr(23);
					$o.my().root.trigger("commit");
				}

				function preview (c){
					if(parseInt(c.w) > 0) {
						// Show image preview
						var img = $img[0], cs, ctx, cs2, ctx2, 
							k = n.k,
							w = c.w*k, // scale width/height
							h = c.h*k; 
						if (w>width*2) {
							cs2 = $c2[0];
							cs2.width=width*2;
							cs2.height=2*width*(h/w);
							ctx2 = cs2.getContext("2d");
							ctx2.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, cs2.width,cs2.height);

							cs = $c[0];
							cs.width=width;
							cs.height=width*(h/w);
							ctx = cs.getContext("2d");
							ctx.drawImage(cs2, 0, 0, cs2.width,cs2.height, 0, 0, cs.width, cs.height);
						} else {
							cs = $c[0];
							cs.width=width;
							cs.height=width*(h/w);
							ctx = cs.getContext("2d");
							ctx.drawImage(img, c.x*k, c.y*k, w, h, 0, 0, cs.width, cs.height);
						}

					}
				}
			}

Export.init:
function ($form, form) {
		var d=form.data, 
				//
				$x = $('<div></div>'),
				$src, 
				$pane=$('<div class="cw-medium-pane vat dib w200 ml50 fs90 lh120 blue" '
								+'style="position:fixed"></div>'),
				keys={};

		var that = this, 
				$m = $(".cw-app-active #medium"), 
				$e = $(".cw-app-active #meditor"),
				$i = $m.find(">span.cw-item"),
				shtml=[], ocss={}, orepo={};

		this.CSSCompiler($e, ocss)

		$i.each(function(){
			var $f = $(this), 
					data = $f.data(),
					type = $f.my("data").type,
					man = data.my.manifest.Items[type],
					compiler = (man.Compiler || that.Compiler);
			try {
				compiler.call( that, $f, shtml, ocss, orepo, man);
			} catch(e) {
				console.log(e.message, e.stack);
				shtml.push('<p class="red">Error compiling #my-form-'+data.my.cid+' form</p>');
			}
		});

		$src= $(shtml.join("\n\n\n"));

		$src.appendTo($x);
		if ($src.find("h2,h3").each(function(i){
			var $h = $(this),
					style = $h[0].tagName==="H2"?"hoverlink mb7":"hoverlink mb7 fs90 ml10 lh120",
					h = $h.text().toLowerCase()
						.replace(/[^a-z0-9\s]/g,"").compact()
						.replace(/\s/g,"-").to(10);
			if (!h) h="header"+i;
			if (keys[h]) h=h+"-"+Number.random(10,99);
			keys[h]=true;
			h = "CW-"+h;

			$('<div class="'+style+'"><a class="pseudolink" href="#'
					+h+'">'+$h.text()+'</a></div>').appendTo($pane);
			$(this).attr("id",h);
		}).size()) {
			$pane.prepend('<div class="fi-bookmark fs200 mb2 salmon "></div>');
		};

		d.html='<style>\n'
				+Object.values(ocss).join("\n")
			+'</style>\n\n'
			+'<div class="w750 dib vat my-manifest-1dn1yeso">'
			+$x.html()
			+'</div>\n'
			+'\n\n<!-- Sidepane -->\n\n'+$pane[0].outerHTML+'\n';

		$form.html('<textarea id="html"></textarea>');
	}

Export.Compiler:
function ($form, html, css, repo, manifest) {
		// Default item to HTML compiler.
		// Gets HTML content of form,cleans it and pushes HTML to html array.
		// Also puts <style> content (if any) into css obj under appropriate keys.

		var $src = $($form[0].outerHTML.replace(/contenteditable\s?=\s?"true"/g,""));
		$src.find("textarea,input,select,button,.hide,.cw-hoverpane").remove();
		html.push($src[0].outerHTML);

