manifest.init:
function ($o, form) {
		var i, j, that = this, 
				d = form.data.doc,
				items = d.items;

		$o.html(this.HTML);

		// prepare doc if it’s empty
		if (!d.items.length) {
			d.items.push( 	$.extend(true, {},that.Item.data, {id:that.GenId()}) );
		}

		// extend runtime
		that.AddItem.Typelist = Object.keys(that.Types).remove("attachment");
		that.Active = d.items[0].id;
		that.List.push(d.items[0]);
		that.getDup = function (item, name0) {
			var i,e, ename, fname = that.Path(item, name0), u=false;
			for (var i=0;i<d.items.length && !u; i++) {
				e = d.items[i];
				ename = that.Path(e);
				if (e.id!==item.id && ename===fname) u = e.type;
			}
			return u;
		};
		that.gzip = that.Gzip();

		//remember mimes && set missing cache max-ages
		for (i=0;i<d.items.length;i++) {
			j = d.items[i];
			if (j.type==="attachment" && !j.mime) {
				j.mime = d._attachments[(j.folder?j.folder+"/":"")+j.name+"."+j.ext].content_type;
			}
			if (!j.cache) j.cache = "86400";
			if (!j.state) j.state = {savedSdbm:"",sdbm:"",depends:{},impacts:{}};
			delete j.savedSdbm; delete j.depends;
		}

		// delete item event handler

		$o.on("delitem.my", function(evt, item){
			var ipos = d.items.findIndex(_find),
					lpos = that.List.findIndex(_find),
					fname = that.Path(item);
			if (d.items.length>1) {
				d.items.removeAt(ipos);
				that.List.removeAt(lpos);
				if (d._attachments && d._attachments[fname]) 
					delete d._attachments[fname];
				that.Expanded[item.id] = false;
				that.Active = that.List[0].id;
				$o.find("#items").trigger("recalc");
			}
			else {
				(window.l||cw.lib).note("Can’t remove last resource.","error");
			}

			function _find (e) {
				return e.id == item.id;
			}
		});

	}

manifest.UploadSite:
function (doc, $root, uploadList) {
	AWS.config.update(this.creds.s3);
	var i, j, that = this,
			s3 = new AWS.S3({params: {Bucket: doc.site.s3bucket}}),
			_gzip = that.gzip,
			totalSize = 0,
			sendAtt = {},
			$cs = $root.find("#counter-size"),
			items = {},
			res; 

	// cache items
	for (i=0;i<doc.items.length;i++) {
		j = doc.items[i];
		items[(j.folder?j.folder+"/":"")+j.name+"."+j.ext] = doc.items[i];
	}

	res = PushToBucket(doc, function(total, send){
		totalSize = total;
		sendAtt = send;
		$cs.trigger('upload');
	});

	// section to animate downloading
	// spinning circle
	setTimeout(function () {
		if (res.state() === "pending") $root.addClass('my-app-busy');
	}, 100);

	// size counter
	setTimeout(function () {
		if (res.state() === "pending") {$root.addClass('my-app-busy-high');$cs.html("");}
	}, 200);

	// remove block from app after upload
	res.always(function () {
		$root.removeClass('my-app-busy my-app-busy-high');
		$root.find('#counter-size').off('.upload');
	});

	// handler for uploading status
	$cs.on('upload', function () {
		var loadedSize = 0;
		Object.keys(sendAtt).forEach(function (e) {
			loadedSize += sendAtt[e];
		});
		if (loadedSize > 5120 && totalSize > loadedSize) $(this).html(loadedSize.bytes(2) + ' / ' + totalSize.bytes(2));
	}.debounce(50));

	return res;

	//- - - - - - - - - - - - - - - - - - - - - - - -

	function PushToBucket (doc, onProgress) {
		var dfd = $.Deferred(),
				len = Object.size(uploadList || doc._attachments || {}),
				prefix = doc.site.s3folder?doc.site.s3folder.replace(/\/$/,'')+"/":"",
				ctr = 0, // counter for resolving $.Deferred
				total = 0, // total size uploaded files
				send = {}, // object for updating file upload size
				log = {},
				func = {
					onProgress: function (load, name) {
						send[name] = load;
						if (onProgress) onProgress(total, send);
					},
					onDone: function (len, name) {
						if (len) send[name] = len;
						if (onProgress) onProgress(total, send);
					},
					onError: function (name) {
						var idx = name.from(prefix.length);
						log[idx] = "Upload failed";
						if (items[idx]) items[idx].state.savedSdbm = "";
						dfd.notify('Unable to upload file ' + name + '');
						dfd.reject(log);
					},
					onSuccess: function (name) {
						var i, idx = name.from(prefix.length);
						log[idx] = true;
						if (items[idx]) items[idx].state.savedSdbm = items[idx].state.sdbm;
						dfd.notify('File ' + name + ' uploaded succesfully.');
						if (++ctr == len) {
							console.log(log);
							// mark snippets as succeded
							for (i in items) {
								if (items[i].snippet) items[i].state.savedSdbm = items[i].state.sdbm;
							}
							dfd.resolve(log);
						}
					}
				};

		// we need to determine in what environment we are
		if (!window.l && window.cw) {

			Object.keys(uploadList || doc._attachments).forEach(function (e) {
				console.log(prefix + e);
				var params = {
					name: prefix + e,
					content_type: doc._attachments[e].content_type,
					body: doc._attachments[e].data,
					length: doc._attachments[e].data.length/4*3,
					type: "base64",
					gzip: !/(jp[e]?g|png|zip|gif|rar|pdf)$/.test(e),
					cache:items[e].cache||""
				};
				if (doc._attachments[e].length || doc._attachments[e].size) {
					params.length = doc._attachments[e].length || doc._attachments[e].size;
					total += params.length;
				}
				UploadToS3(PrepareToUpload(params), s3, func)
			});
		} else {
			Object.keys(uploadList || doc._attachments).forEach(function (e) {
				var wl = window.location,
						db = that.Db,
						loc = wl.protocol + '//' + wl.hostname + '/x/' + db + '/' + doc._id + '/' + e;

				total += doc._attachments[e].length * 2;
				GetBlob(loc,
				{
					onProgress: function (load) {
						send[e + "send"] = load;
						if (onProgress) onProgress(total, send);
					}
				}, 
				function (body, gzipped) {
					UploadToS3(PrepareToUpload({
						name: prefix + e,
						content_type: doc._attachments[e].content_type,
						length: doc._attachments[e].length,
						body: body,
						type: "u8array",
						gzip: gzipped,
						cache:items[e].cache||""
					}), s3, func)
				});
			});
		}

		return dfd;
	}

	//- - - - - - - - - - - - - - - - - - - - -

	function PrepareToUpload (params) {
		var obj = {
			Key: params.name,
			ACL: 'public-read',
			ContentType: params.content_type
		};

		if (params.cache+"" != "") obj.CacheControl = "max-age="+params.cache;

		if (params.length) obj.ContentLength = params.length;
		if (params.type === "base64") {
			if (params.gzip) params.body = _gzip.base64(params.body);
			else $.my.f.base642blob(params.body,function(b){params.body=b});
		} 
		else if (params.type === "u8array" ) {
			if (params.gzip) params.body = _gzip.u8array(new Uint8Array(params.body));
		}
		obj.Body = params.body;
		if (params.gzip) obj.ContentEncoding = "gzip";
		return obj;
	}

	//- - - - - - - - - - - - - - - - - - - - 

	function UploadToS3 (params, s3, func) {
		var req = s3.putObject(params,function(err){
			if (err) {
				if (func.onError) func.onError(params.Key);
			} else {
				if (func.onSuccess) func.onSuccess(params.Key);
			}
		});
		if (func.onProgress) req.on('httpUploadProgress',function(pr,res){
			func.onProgress(pr.loaded, params.Key);
		});
		if (func.onDone) req.on('httpDone',function(){
			func.onDone(params.ContentLength, params.Key);
		});
	}

	// - - - - - - - - - - - - - - - - - - - -

	function GetBlob (url, params, callback) {
		if (!params.notGzip) var gzipped = !/(jp[e]?g|png|gif|zip|rar|pdf)$/.test(url);

		var oReq = new XMLHttpRequest();
		oReq.open("GET", url, true);
		if (params.onProgress) oReq.onprogress = function(e) {
			params.onProgress(e.loaded);
		};
		if (!gzipped) oReq.responseType = "blob";
		else oReq.responseType = "arraybuffer";
		oReq.onload = function() {
			callback(oReq.response, gzipped);
		};
		oReq.send();
	}

		}

manifest.Gzip:
function (){

	var U8A = window.Uint8Array ? Uint8Array : Array,
			BASE64_MARKER = ';base64,',
			BASE64_MARKER_L = BASE64_MARKER.length;

	function stringToByteArray(str) {
		var i = 0, max = str.length, array = new Array(max);
		while(i < max) array[i] = str.charCodeAt(i++) & 0xFF;
		return new U8A(array);
	}

	function base64ToByteArray(dataURI) {
		var base64Pos = dataURI.indexOf(BASE64_MARKER),
				base64Index = base64Pos != -1 ? base64Pos + BASE64_MARKER_L : 0,
				base64 = dataURI.substring(base64Index),
				raw = window.atob(base64),
				rawLength = raw.length,
				array = new Array(rawLength),
				i = 0;
		while(i < rawLength) array[i] = raw.charCodeAt(i++);
		return new U8A(array);
	}

	function toGzip(data) {
		if(!data) {
			console.log('No data to gzip!');
			return null;
		}
		return new Blob([
			new Zlib.Gzip(data).compress()
		]);
	}

	return {
		'string': function(text) {
			if(!text || !text.length) {
				console.log('No text to gzip!');
				return null;
			}
			return toGzip(stringToByteArray(text));
		},
		'u8array': function(array) {
			if(!array || !array.length) {
				console.log('No u8array to gzip!');
				return null;
			}
			return toGzip(array);
		},
		'base64': function(base64) {
			if(!base64 || !base64.length) {
				console.log('No base64 to gzip!');
				return null;
			}
			//console.log(base64);
			return toGzip(base64ToByteArray(base64));
		}
	}

		}

manifest.Items2Atts:
function (doc, Types, buildAll) {
	var i, j, e, item, t = Types, names = {}, dirty = {},
			aname="", a={}, sn={}, pp={}, src = {}, fin = {}, err={},
			incls = {};

	if (!doc._attachments) doc._attachments = {};

	// gen work structs
	for (i=0;i<doc.items.length;i++) {
		e = doc.items[i];
		if (e) {
			aname = (e.folder?e.folder+"/":"")+e.name+"."+e.ext;
			names[aname]=true;
			sn[aname]=e.snippet;
			src[aname]=doc.items[i];
			if (Object.isFunction(t[e.type].postprocess)) pp[aname] = t[e.type].postprocess;
		}
	}


	// stringify
	for (i in names) _convert (i);


	// Resolves include directives.
	// Possible syntaxes are:
	//  			– inserts as is
	// {/**/} – inserts as is
	// [/**/] – inserts as is
	// "" 		– inserts stringified version

	// parse includes
	for (i in a) _parseIncludes (i);

	// merge snippets deps into files deps
	for (i in a) {
		if (!sn[i]) {
			e = src[i]; 
			var tmp = {};
			for (j in e.state.depends) 
				if (sn[j] && src[j].state.depends) 
					$.extend(tmp, src[j].state.depends);
			$.extend(e.state.depends, tmp);
		}
	}

	// merge files deps into files deps
	for (i in a) {
		if (!sn[i]) {
			e = src[i]; 
			if (e) {
				var tmp = {};
				for (j in e.state.depends) 
					if (a[j] && !sn[j] && src[j].state.depends) 
						$.extend(tmp, src[j].state.depends);
				$.extend(e.state.depends, tmp);
			}
		}
	}

	// build impacts (reverse deps)
	for (i in a) {
		e = src[i];
		if (e) {
			for (j in e.state.depends) {
				if (src[j]) src[j].state.impacts[i] = true;
				else console.log("Missig include "+j+" in "+i);
			}
		}
	}

	// build list of items to be updated
	var imps = {};
	for (i in a){
		e = src[i];
		if (e.state.sdbm !== e.state.savedSdbm) {
			$.extend (true, imps, e.state.impacts);
			if (!e.snippet) $.extend (true, dirty, e.state.depends);
			dirty [i] = true;
		}
	}
	// add dependees to dirty list, 
	// they also need to be updated
	for (i in imps) {
		e = src[i];
		if (e) {
			if (!e.snippet) $.extend (true, dirty, e.state.depends);
			dirty [i] = true;
		}
	}

	console.log("Dirty", dirty);

	// build snippets
	for (i in dirty) if (sn[i]) _resolveIncludes (i);

	// build files
	for (i in dirty) if (!sn[i]) _resolveIncludes (i);

	// build attachments
	for (i in dirty) {
		if (!sn[i]) {
			a[i].data = $.my.f.base64(fin[i], false, false);
			doc._attachments[i] = a[i];
		}
	}

	var process={};
	for (i in src) {
		if (
			src[i].type ==="attachment" &&
			src[i].state.sdbm !== src[i].state.savedSdbm
		) process[i] = true;
		if (!sn[i] && dirty[i]) process[i] = true;
	}

	return {
		err:err,
		dirty: dirty,
		doc:doc,
		upload:process
	};


	//- - - - - - - - - - - - - - - - - - - - - - - -
	function _parseIncludes (iname) {
		var reSnippet = 
		/([\{\[]\/\*\{\{[a-z0-9\-\.\/]+\.[a-z0-9\-]+\}\}\*\/[\}\]])|("\{\{[a-z0-9\-\.\/]+\.[a-z0-9\-]+\}\}")|(\{\{[a-z0-9\-\.\/]+\.[a-z0-9\-]+\}\})/gi,
				i = iname, deps,
				j, re, idx, str, 
				incl = a[i].data.match(reSnippet);

		deps = src[i].state.depends={};
		if (incl && !src[i].noIncludes) {		
			incls[i] = incl.slice(0);
			for (j=0;j<incl.length;j++) {
				idx = incl[j].replace(/(\/\*|\*\/)/gi,"").replace(/[^a-z0-9\-\.\/]/gi,"");
				deps[idx] = true;
			}
		}
	}


	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _resolveIncludes (iname) {
		var i = iname, 
				j, re, idx, str, 
				reSnippet = 
		/([\{\[]\/\*\{\{[a-z0-9\-\.\/]+\.[a-z0-9\-]+\}\}\*\/[\}\]])|("\{\{[a-z0-9\-\.\/]+\.[a-z0-9\-]+\}\}")|(\{\{[a-z0-9\-\.\/]+\.[a-z0-9\-]+\}\})/gi,
				incl = a[i].data.match(reSnippet);

		fin[i] = a[i].data;
		if (incl) {			
			for (j=0;j<incl.length;j++) {
				idx = incl[j].replace(/(\/\*|\*\/)/gi,"").replace(/[^a-z0-9\-\.\/]/gi,"");
				re = new RegExp(RegExp.escape(incl[j]), 'g');
				if (idx === i) fin[i] = fin[i].replace(re,"");
				else if (a[idx]) {
					str = fin[idx]||a[idx].data;
					if (incl[j].to(1) === '"' && incl[j].from(-1) ==='"') str = JSON.stringify(str);
					fin[i] = fin[i].split(re).join(str);
				} 
				else fin[i] = fin[i].replace(re,"");
			}
		}
		if (pp[i]) {
			try { fin[i] = pp[i](src[i], fin[i]);  }
			catch (e) {
				_e(i, "Post-processor error, "+e.message);
				console.log(e.message, e.stack, e);
				window.M = fin[i];
			}
		}
	}

	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _convert (iname) {
		var it=src[iname], att,
				parser;

		if (it.type!="attachment") {			
			// Item			
			if(it.parserActive) {
				try { parser = eval(it.parser); } catch (e){ parser = null; }
				if (!Object.isFunction(parser)) {
					_e (iname,"Invalid parser.");
					parser = t[it.type].stringify;
				}
			} 
			else parser = t[it.type].stringify;

			try {
				att = {
					content_type:it.mime||t[it.type].mime,
					data:parser(it, doc._attachments, doc)+""
				};
			} catch(e) {
				_e(iname,"Error parsing data, "+e.message);
			}
			if (att ) {
				a[iname] = att;
				it.state.sdbm = $.my.f.sdbmCode(Object.reject(it, ["state"]));
			}
		}
		else {
		// We have attachment
			if (doc._attachments && (att = doc._attachments[iname])) {
				if (att.digest) it.state.sdbm = $.my.f.sdbmCode(att.digest);
			} else {
				it.state.sdbm = "Z";
			}
		}
	}

	//- - - - - - - - - - - - - - - - - - - - - - - -

	function _e (iname, error) {
		// Logs error
		if (!err[iname]) err[iname] = [];
		err[iname].push(error);
	}

		}

manifest.ResolveExternals:
function () {
	var that = this, pis,
			async,
			rows = that.data.doc.items,
			z = $.Deferred();

	pis = rows.map(function(e,i){
		var s="", id='', p = e.external, pi;
		if (p) {
			async = true;
			if (p.startsWith("pouch://")) {
				p = p.from(8).split(/\s+/);
				s = p[1]||'';
				id = p[0];
				pi = $.Deferred();
				that.db.load(id, true).then(
					function(doc) {
						if (s) {
							var r = cw.lib.getref(doc, s);
							if (r) {
								pi.resolve(r);
							} else {
								pi.reject("Failed to load "+p+", selector not found");
							}
						} else {
							pi.resolve(doc);
						}
					},
					function(){
						pi.reject("Failed to load "+p);
					}
				)

				return pi.promise();

			}
		} else {
			return e.data;
		}
	});

	if (!async) z.resolve();
	else {
		Q.all(pis)
		.then(
			function(reps){
				reps.forEach(function(e,i){

					if (Object.isString(e)) {
						rows[i].data = e;
					}
					else {
						if (Object.isObject(e) && rows[i].ext=='js') {

							rows[i].data = cw.lib.js2txt(cw.lib.unjson(e),'\t');

						} else {
							rows[i].data = JSON.stringify(e,null,'\t');
						}
					}

				});
				z.resolve();
			},
			function(e){
				z.reject ("Failed to load ext resource","error");
			}
		)
	}

	return z.promise();
		}

