js.postprocess:
function (item, att){
		if (!item.Uglify) return att;
		else {
			// compress and uglify 
			var u = UglifyJS,
					ast = u.parse(att+"");
			ast.figure_out_scope();
			ast = ast.transform(u.Compressor());
			ast.figure_out_scope();
			ast.compute_char_frequency();
			ast.mangle_names();
			return (item.desc?"/*"+item.desc.replace(/(\/\*|\*\/+)/g,'')+"*/\n":"") + ast.print_to_string();
		}
	}

