({
	delay:50,

	//- - - - - - - - - - - - - - - - - - - - -

	init: function($o, form){
		var that = this, idx, 
				d = form.data.doc;


		$o.on("click.my", ".siteitem", function(){
			var $l = $(this),
					id = $l.attr("id").from(3),
					item = d.items.find (function(e){
						return e.id === id;
					});
			if (!item) {
				if (that.Expanded[id]) that.Expanded[id] = false;
				else that.Expanded[id] = true;
				$o.trigger("check");
			}
			else /*if (item.type!="attachment")*/ {
				that.Active = id;
				if (item.folder) that.Expanded[item.folder] = true;
				if ( (idx = that.List.indexOf(item)) !== -1) {
					that.List.removeAt(idx);
				} 
				that.List.unshift(item);
				$o.trigger("check");
			}
		})
	},

	///- - - - - - - - - - - - - - - - - - - - -

	bind:function(d,v,$o) {
		var that = this, 
				i, e, html="",
				list = d.doc.items, 
				res = [], fl = "";

		// sort list
		list = list.sortBy(function(e){
			return [
				(!e.folder) *1,
				e.folder,
				e.snippet*1,
				e.name,e.type
			].join("/").toLowerCase();
		});

		// gen items/folders final list
		for (i=0; i<list.length; i++) {
			e = list[i];
			if (e.folder && e.folder != fl) {
				fl = e.folder;
				res.push({
					id:fl,
					html: '<span class="dib w15">'+(that.Expanded[fl]?"▾":"▸")+'</span>'+fl,
					css:"xgray",
					type:"folder"
				});
			} 
			if (!e.folder || that.Expanded[e.folder]) {
				var isdirty = (!e.state.savedSdbm || e.state.sdbm == "Z"  || (e.state.sdbm!==e.state.savedSdbm));
				res.push({
					id:e.id,
					html: (e.folder?"":"&nbsp;")
					+ (e.parserActive?'<i>':"")
					+ (isdirty?"›&nbsp;":"")+e.name+"."+e.ext
					+ (e.parserActive?'</i>':""),
					css: (e.external?"orange o80":e.snippet?"sky":e.type==="attachment"?"gray":"blue") + (e.id == that.Active?" bg-lblue":""),
					type:e.snippet?"snippet":"item",
					title: e.folder+"/"+e.name+"."+e.ext,
					folder: e.folder
				});
			}
		}

		// render list
		for (i=0;i<res.length;i++) {
			e = res[i];
			html+='<div class="siteitem w180 oh '+e.css+(e.folder?" pl15":"")
				+'" id="sx-'+e.id+'" title="'
				+(e.title?e.title:"")+'">'
				+e.html
				+'</div>';
		}
		return html;
	}
