	// Publish	

	css:{
		":disabled":function(d){
			var s3 = $.extend(!0, {}, d.doc.s3, cw.crypto.dec(d.doc.creds)["s3"]||{}), 
					s = d.doc.site;
			return (!s3.accessKeyId || !s3.secretAccessKey || !s3.region || !s.s3bucket);
		}
	},

	watch:"#sname",

	bind: function (d, v, $o){
		var i, async = false,
				note = cw.lib.note,
				that = this,
				rows, key,
				err = {}, atts2go,
				$root = $o.my().root;
		if (v!=null) {
			if (key = d.doc.creds.crypto) {
				if (!cw.crypto.has(key)) {
					cw.lib.note("No cryptokey to decode S3 credentials.", "error");
					return;
				} else {
					var s3 = cw.crypto.dec(d.doc.creds)["s3"];
					if (s3) {
						that.creds.s3 = s3;
					} else {
						cw.lib.note("Error decoding S3 credentials.", "error");
						return;
					}
				}
			} else {
				that.creds.s3 = Object.clone(d.doc.s3, !0);
			}
			that.ResolveExternals()
			.then(_createAtts)
			.fail(function(e){
				console.log(e);
				note("Failed to load ext resource","error");
			});
		}

		// - - - - - - -

		function _createAtts (){
			try {
				atts2go = that.Items2Atts(d.doc, that.Types);
				err = atts2go.err;
			} catch (e) {
				console.log(e.message, e.stack, e)
			}
			if (Object.size(err)) {
				$o.modal({
					manifest:this.ParseErrors,
					data:atts2go,
					esc:true,
					enter:true,
					width:600,
					global:true,
					screen:true
				}).then(function(){
					_getEnv();
				})
			} 
			else {
				_getEnv();
			}
		}

		// - - - - - - -

		function _getEnv () {

			if (that.db) {
				var Att = "_attachments";
				cw.db(that.db.name).save(d.doc, true)
				.then(function(newdoc){
					d.doc._rev = newdoc._rev;
					d.doc._attachments = newdoc._attachments
					that.db.load(d.doc._id, true).then(function(res){
						var obj = $.extend({}, Object.reject(d.doc,Att), Object.select(res,Att));

						_go(obj).then(function(msg){

							for (var i=0; i<obj.items.length; i++) {
								d.doc.items[i].state.savedSdbm = obj.items[i].state.savedSdbm;
							}	

							that.db.save(d.doc).then(function(newdoc1){
								d.doc._rev = newdoc1._rev;
								d.doc.stamp = newdoc1.stamp;
								_success(msg);
								$root.find("#items").trigger("recalc");
							});
						}, _fail);
					});
				});
			}
			else {
				_go().then(_success, _fail);
			}
		}

		// - - - - - - - 

		function _go(doc){
			var pi = $.Deferred();
			if (!Object.size(atts2go.upload)) {
				pi.resolve("All files are up to date, nothing to upload.");
			} else {
				var res = that.UploadSite(doc || d.doc, $o.my().root, atts2go.upload);

				res.then(function(d){
					pi.resolve('Files uploaded succesfully.');
				}, function () {
					pi.reject('Failed to publish files. Please, try again.');
				}, function (notify) {
					console.log(notify);
				});
			}
			return pi.promise();
		}

		// - - - - - - - 

		function _success (msg){
			note(msg);
		}

		function _fail (msg){
			note(msg,'error');
		}
		// - - - - - - - 

	}
})

({
	bind: function(d,v,$o) {
