		if (v!=null) {
			that.db.load(d.doc._id, true)
			.then(
				function(doc){

					var i, T = that.Tar.Tar(),
							tar = new T(), 
							a = doc._attachments,
							url,
							r;

					if (Object.size(a)) {

						for (i in a) {
							r = tar.append(i, that.Tar.Bytes(a[i].data));
						}

						try {
							url = URL.createObjectURL(new Blob([r]));
						} catch(e) {
							console.log(e, e.stack);
						}
						if (url) {
							$o.modal({
								manifest:{
									init:[
