	doc:{
		_id:"",
		name:"Default site",
		type:"s3site",
		owners:[],
		acl:[],
		s3:{
			accessKeyId :"",
			secretAccessKey :"",
			region :"eu-west-1"
		},
		site:{
			s3bucket:"",
			s3folder:"",
			siteBase:"",
			previewBase:""
		},
		creds:{
			crypto:""
		},
		items:[]
	},
	cmd:""
