manifest.MemState:
function (node) {
		var d = this.data, old; 
		if (!d.viewstate) this.data.viewstate={};
		old = d.viewstate[node.path.join('❀')+'❀'+node.key];
		if (old != node.state) {
			d.viewstate[node.path.join('❀')+'❀'+node.key] = node.state;
			if (d._id) this.MemStateSave();
		}
	}

manifest.MemStateSave:
function (){
		// debounced by init
		var d = this.data;
		if (d._id && Object.size(d.viewstate)) {
			this.Ls("viewstate."+d._id, {stamp:Date.now(), state:d.viewstate});
		}
	}

manifest.init:
function ($o, form) {
	var i, that = this,
			d = form.data,
			dur = Date.now();


	// Singletone constructors init
	that.Ls = that.Ls("");
	if (Object.isFunction(that.Db)) that.Db = that.Db($o);

	// Render HTML
	$o.html(that.HTML.Main);

	// Debounce viewstate saver
	that.MemStateSave = that.MemStateSave.debounce(700);

	// First recursion step
	$.extend(that.Doc, {
		state:2,
		level:0,
		path:[],
		type:"object",
		key:"Root",
		value:form.data.doc
	});

	d._id = d.doc._id||"";

	// Global handlers for buttons
	["add","takeout","copy"].each(function(e){
		$o.on("click.my", ".btn-"+e, that.Event[e].fill(undefined, undefined, that, $o))
	});

	// Clean doc
	$o.on("click.my", "#doc>.iact>.btn-del", function(evt){
		evt.stopPropagation();
		$o.trigger("cancel");
	});

		}

manifest.die:
function (){
		var i, b = this.Blobs;
		// Free blobs objURLs cache
		for (i in b) URL.revokeObjectURL (b[i]);
	}

manifest.MakeList:
function (data, key, state){

	// Object/array processor,
	// generates typed metaobject

	var that=this,
			dt, a=[], man, bin, 
			hash, b = data.value;
	for (var i in b) {
		dt = $.type(b[i]);

		// Detect multy
		man = /^(ob|ar)/.test(dt)?"multi":dt;

		// Detect string-derived types
		if (dt==="string"){
			if (/^function\s?\([^\)]*\)\s*\{[\s\S]*\}$/.test(b[i])) {
				man = "function";
			}
			else if(b[i].length>160 && /^[a-zA-Z0-9\+\/]+={0,2}$/.test(b[i])) {
				try {
					bin = atob(b[i]);
					hash = $.my.f.sdbmCode(b[i]);
					man = "base64";
				}catch(e){}
			}
		}

		// Do we have viewstate stashed?
		var vs = that.data.viewstate,
				path = data.path.slice(0).add(data.key),
				vkey = path.join('❀')+'❀'+i;

		a.push({
			state:state?state:(vs && vs[vkey]?vs[vkey]:1),
			level:data.level+1,
			manifest:"$"+man,
			path:path,
			type:dt,
			key:i,
			id:i,
			value:b[i],
			bin:bin,
			hash:hash
		})
	}
	return a;
		}

manifest.Rejoin:
function (d, list) {
	for (var r, i=0;i<list.length;i++) {
		r=list[i];
		if (!/^(ob|ar)/.test(r.type)) d.value[r.key] = r.value;
	}
		}

manifest.Ls:
function (){

	// Local storage manager
	var ls = window.localStorage;

	var storage =  function LS (key0, data){
		var x,  key = "JSON.Editor."+key0;
		if (data) {
			return ls.setItem(key, JSON.stringify(data));
		}
		else if (data===null || data===false) {
			return ls.removeItem(key);
		}
		else {
			var x = ls.getItem(key);
			if (Object.isString(x)) return JSON.parse(x);
			else return null;
		}
	};

	return storage;
		}

