Event.add:
function (evt, data, that, $o){
	evt.stopPropagation();
	var $b = $(this),
			$f = $b.parents(".my-form").eq(0);		
	var $p = $b,
			p = $f.data("my").data,
			mn = $f.data("my").manifest,
			t = $.type(p.value),
			isa = t=="array",
			key = $f.my("data").key,
			clip = that.Ls("Clip")||{type:""},
			sample = {
				object:{},
				array:[],
				string:"",
				boolean:true,
				number:0
			};
	$b.parent().addClass("hover");
	$f.addClass("hover");
	$(this).modal({
		manifest:that.MAdd,
		data:{
			text:'New '+(isa?'member':'property')+' in <b>'+p.key+':'+(isa?'[]':'{}')+'</b>',
			prepend:(isa?'Insert':'Prepend'),
			append:(isa?'':'Append'),
			isarray:isa,
			key:isa?"0":'',
			clip:clip,
			keys:isa?{}:Object.keys(p.value).reduce(function(a,b){a[b]=true;return a;},{})
		},
		width:300,
		align:"top:100%",
		root:$o,
		bound:10,
		nose:"top",
		screen:"rgba(255,255,255,0)",
		close:false,
		esc:true,
		enter:true
	})
	.then(function(data){
		var i, cmd = data.cmd,
				redraw = true, 
				keys=[], vals=[];

		if (data.clip.type && data.type=="clip") sample.clip = data.clip.value; 

		if (p.level==1 && /^(_id|_rev|creator|created|crypto)$/.test(data.key)) {
			cw.lib.note("Can’t change system properties.","error");
			return;
		}

		// Object
		if (/^(ob)/.test(t)) {

			// Append/replace is simple
			if (cmd=="append" || p.value[data.key]!=null) { 
				p.value[data.key] = Object.clone(sample[data.type],true); 
			}

			// Prepend is more complex
			else if (cmd=="prepend") {

				keys = Object.keys(p.value);
				for (i in keys) {
					vals.push(p.value[keys[i]]);
					delete p.value[keys[i]];
				}
				p.value[data.key] = Object.clone(sample[data.type],true);
				for (i in keys) {
					if (keys[i]!==data.key) p.value[keys[i]] = vals[i];
				}
				vals=keys=null;
			}
			//Unknown cmd
			else redraw = false;
		}

		// Array
		else if (isa) {
			p.value.insert(Object.clone(sample[data.type],true),data.key);
		}
		else redraw = false;

		if (redraw) {
			// force redraw
			mn.ShowItems = null;
			mn.List = that.MakeList(p);
			p.state=2;
			$f.find(".idesc:eq(0)").trigger("check");
			$f.find(".ilist:eq(0)").trigger("recalc");
		}
	})
	.always(function(){
		$b.parent().removeClass("hover");
		$f.removeClass("hover");
	});
			}

Event.takeout:
function (evt, data, that, $o){
	evt.stopPropagation();
	var $b = $(this),
			$f = $b.parents(".my-form").eq(0),
			p = $f.data("my").data,
			mn = $f.data("my").manifest;
	$b.parent().addClass("hover");
	$f.addClass("hover");
	$b.modal({
		manifest:that.MTakeout,
		data:{
			json:JSON.stringify(p.value,null,'  '),
			path:p.path,
			key:p.key,
			replace:Object.size(p.value)?"Replace":"Save",
			extend:Object.size(p.value)?"Extend":""
		},
		width:800,
		align:"top:-3px",
		root:$o,
		bound:0,
		nose:"",
		screen:"rgba(255,255,255,0)",
		close:true,
		esc:true,
		enter:true
	})
	.then(function(res){
		var redraw = true;

		var res1 = Object.reject(
			res.node,
			p.level==0?[/^(_id|_rev|creator|created|crypto)$/]:[]
		);
		if (res.cmd=="extend") {
			Object.merge( p.value,  res1, true);
		}
		else if (res.cmd=="replace") {
			var temp = Object.select(p.value, /^(_id|_rev|creator|created|crypto)$/);
			that.Root().Lib.kick(p.value, res1);
			if (p.level == 0) Object.merge(p.value, temp);
		}
		else redraw = false;

		if (redraw) {
			// force redraw
			mn.ShowItems = null;
			p.state=2;

			that.Root().Lib.relist($f, mn);
			/*mn.List = that.Root().MakeList(p);
			$f.find(".idesc:eq(0)").trigger("check");
			$f.find(".ilist:eq(0)").trigger("recalc");*/
		}

	})
	.always(function(){
		$b.parent().removeClass("hover");
		$f.removeClass("hover");
	});
			}

