		if (!d.viewstate) this.data.viewstate={};
		old = d.viewstate[node.path.join('❀')+'❀'+node.key];
		if (old != node.state) {
			d.viewstate[node.path.join('❀')+'❀'+node.key] = node.state;
			if (d._id) this.MemStateSave();
		}
