(function(data, key, state){

	// Object/array processor,
	// generates typed metaobject

	var that=this,
			dt, a=[], man, bin, 
			hash, b = data.value;
	for (var i in b) {
		dt = $.type(b[i]);

		// Detect multy
		man = /^(ob|ar)/.test(dt)?"multi":dt;

		// Detect string-derived types
		if (dt==="string"){
			if (/^function\s?\([^\)]*\)\s*\{[\s\S]*\}$/.test(b[i])) {
				man = "function";
			}
			else if(b[i].length>160 && /^[a-zA-Z0-9\+\/]+={0,2}$/.test(b[i])) {
				try {
					bin = atob(b[i]);
					hash = $.my.f.sdbmCode(b[i]);
					man = "base64";
				}catch(e){}
			}
		}

		// Do we have viewstate stashed?
		var vs = that.data.viewstate,
				path = data.path.slice(0).add(data.key),
				vkey = path.join('❀')+'❀'+i;

		a.push({
			state:state?state:(vs && vs[vkey]?vs[vkey]:1),
			level:data.level+1,
			manifest:"$"+man,
			path:path,
			type:dt,
			key:i,
			id:i,
			value:b[i],
			bin:bin,
			hash:hash
		})
	}
	return a;
})

