({
	id:"Sys.JSON.Base64",
	inherit:["Root"],
	init: function($o, form){
		var sig, that = this, 
				d = form.data;
		$o.html(that.Root().HTML.ItemSimple);

		if (!d.sig) {
			d.sig = sig = atob(d.value.to(40));

			d.size = (d.value.length/4*3).floor();

			if (sig.to(4)==="ÿØÿà") { 
				d.mime =  'image/jpeg'; 
			}
			else if (sig.to(4)==="\u0089PNG") { 
				d.mime = 'image/png'; 
			}

			if (d.mime) {
				// we have mime so its an image
			} else {
				// try to guess it by name
				if (d.path.length==3 && d.path[1]=="_attachments") {
					// we have couchdb doc
					var i, ico="fi-paperclip", mime="application/octet-stream", fname = d.path[2];
					for (i in that.Mime) if (that.Mime[i].test(fname)) mime = i;
					for (i in that.Ico) if (that.Ico[i].test(fname)) ico = i;
					d.mime = mime;
					d.ico = ico;
					d.fname = fname;
				}
			}

			that.Root().Blobs[d.hash] = URL.createObjectURL($.my.f.base642blob(d.value, null, d.mime));
		}

	},
	ui:{
		".ikey:eq(0)":function(d,v){return this.Root().Lib.keybind(d,v)},
		".ivalue:eq(0)":{
			init: function($o, form){
				$o.on("click.my", ".base64string", function(evt){
					if ($(evt.target).is("a")) return true;
					var $n = $o.my("find",".inav");
					if (!$n.hasClass("iseditor")) {
						form.data.state = 2;
						$n.addClass("iseditor");
						$o.attr("contenteditable",true).attr('spellcheck', false);
						$o.text(form.data.value).focus();
					}
				})
				.on("blur.my", function(){
					var $n = $o.my("find",".inav");
					if ($n.hasClass("iseditor")) {
						form.data.state = 1;
						form.data.value = $o.text().stripTags();
						$n.removeClass("iseditor");
						$o.attr("contenteditable",false);
						$o.text('"'+form.data.value.to(140)).trigger("check");

					}
				})
			},
			bind:function(d, v, $o){
				var h="", 
						img = /^image/.test(d.mime+"") && d.value.length < 5e5?this.Root().Blobs[d.hash]:null;
				if (d.state==1) {

					var w = $o.width();

					if (img) {
						h+='<a href="'+img+'" target="_blank" class="base64image"><img src="'+img+'" '
						+'class="fl mr10"/></a>';
						w=w-80;
					} else if (d.mime && d.ico && d.fname){
						h+='<a href="'+this.Root().Blobs[d.hash]+'" '
							+' class="base64ico w55 h55 tac dib vat mr10 lgray fs200 bg-lgray br2" '
							+ 'download="'+d.fname+'" '
							+' title="Download '+d.fname+'" '
						+' style="line-height:55px"><span class="'+d.ico+'"></span></a>';
						w=w-80;
					}

					var trim = ((w/10*2.5)|0).clamp(140,600);

					h+='<div class="dib vat base64string">';
					h+='"';
					if (d.value.length>trim) {
						h+=d.value.truncate(trim)
						+'"&nbsp;<a href="'+this.Root().Blobs[d.hash]+'" '
						+'target="_blank" class="ilabel tdn">'
						+(d.mime?'Open':'Download')
						+'&nbsp;'+d.size.metric(2)+'b</a>';
					}
					else h+=d.value+'"';
					if (img) h+='</div>';
					return h;
				}
				else {
					if (v!=null) d.value = v;
					return d.value;
				}
			},
			events:"input.my"
		}
	},
