({
	data:{
		editor:{
			type:"passkeeper",
			name:"",
			crypto:"-",
			urls:[
				{	url:"", users:[
					{login:"", pass:"", roles:[]}
				] }
			],
			log:[]
		},
		cmd:"" 
	},
	init: function ($app, form) {
		$app.html(this.AppHTML);
	},
	ui: { /* See tab */ }
})

({
	"#name":"editor.name",
	"#crypto":{
		bind:"editor.crypto",
		init: function($o) {
			$o.html(
				[ {id:"-",name:"Select cryptokey"} ]
				.add( cw.crypto.keys() )
				.reduce(function(a,b){
					return a+('<option value="{id}">{name}</option>').assign(b);
				},"")
			).select2();
		}
	},
	"#list":{
		bind:"editor.urls",
		manifest:"Url",
		list:'<div class="item"></div>',
		init: function ($ctrl) {
			$ctrl.sortable({handle:".fi-list"});
		}
	},
	"#btn-save":{
		bind: function (d,v,$o){ 
			if (v!=null) {
				if (!d.editor.crypto || d.editor.crypto.length<6) 
					cw.lib.note("Can’t save – select crypto key.","error");
				else $o.trigger("commit");
			}
		},
		events:"click.my",
		watch:"#name,#crypto",
		css:{
			":disabled":function(d){
				return !d.editor.name || d.editor.crypto=="-";
			}
		}
	},
	"#btn-close":{
		bind:function(d,v,$o){ if (v!=null) $o.trigger("cancel");},
		events:"click.my"
	},
	"#btn-addurl":{
		bind: function(data, val, $ctrl) {
			if (null!=val) $ctrl.my("find","#list").trigger("insert",1e6);
		},
		events:"click.my"
	},
	"#atts":{
		delay:50,
		init: function ($o, form) {
			$o.my("cw.Sys.Attachments", {
				doc:form.data.editor,
				truncate:20
			});
		},
		bind: function (){}
	}
})

